    int[] mParent;

    public int numSimilarGroups(String[] strs) {
        int count = strs.length;
        mParent = new int[count];
        for (int i = 0; i < count; i++) {
            mParent[i] = i;
        }

        for (int i = 0; i < strs.length - 1; i++) {
            for (int j = i + 1; j < strs.length; j++) {
                int findI = find(i);
                int findJ = find(j);
                if (findI != findJ) {
                    int num = 0;
                    for (int k = 0; k < strs[i].length(); k++) {
                        if (strs[i].charAt(i) != strs[j].charAt(k)) {
                            num += 1;
                        }
                        if (num > 2)
                            break;
                    }
                    if (num <= 2) {
                        mParent[findI] = findJ;
                        count -= 1;
                    }
                }
            }
        }

        Utils.printIntegerArray(mParent);
        return count;
    }

    public int find(int i) {
        return mParent[i] == i ? i : (mParent[i] = find(mParent[i]));
    }
